// BoardStatisticsCtrl generates a chart and shows the statistics for an imageboard
angular.module('prim').controller('BoardLogCtrl', ["$scope", "hotkeys", "ModHandlers", "Utils", function($scope, hotkeys, ModHandlers, Utils) {

    // using controllerAs
    var self = this;

    // selects usergroup class
    self.usergroupClass = Utils.usergroupClass;

    self.currentPage = 1;

    // watcher for pagination navigation
    $scope.$watch(function() {
        return self.currentPage;
    }, function(value, old) {
        if (!angular.equals(value, old)) {
            self.getLog(value);
        }
    });

    // set default column and order for table sorting
    self.sort = {
        column: 'log_time',
        desc: true
    };

    // will get the log json
    self.getLog = function(page) {
        // update the page if needed
        self.currentPage = page;
        // Get the audit log json
        ModHandlers.boardlog.get({
            page: page
        }, function(data) {
            self.data = data.boardlog.items;
            self.pagination = {
                totalItems: data.boardlog.total,
                currentPage: data.boardlog.current_page,
                numPages: data.boardlog.pages,
                itemsPerPage: data.boardlog.per_page,
                maxSize: 3
            };
        }, function(error) {
            Utils.apiError(error.status);
        });
    };

    // get the first page
    self.getLog(self.currentPage);

    hotkeys.bindTo($scope)
        .add({
            combo: 'shift+left',
            description: 'Previous Page',
            callback: function() {
                if (self.pagination.currentPage > 1) {
                    var page = self.pagination.currentPage - 1;
                    self.getLog(page);
                }
            }
        })
        .add({
            combo: 'shift+right',
            description: 'Next Page',
            callback: function() {
                if (self.pagination.currentPage < self.pagination.numPages) {
                    var page = self.pagination.currentPage + 1;
                    self.getLog(page);
                }
            }
        });

}]);

// BoardStatisticsCtrl generates a chart and shows the statistics for an imageboard
angular.module('prim').controller('BoardStatisticsCtrl', ["$route", "dateFilter", "ModHandlers", "Utils", function($route, dateFilter, ModHandlers, Utils) {

    // using controllerAs
    var self = this;

    // chart labels
    self.labels = [];
    // the chart series names
    self.series = [];
    // the chart datapoints
    self.chartdata = [];

    self.colors = ['#BF4848', '#4883BF'];

    // chart options
    self.options = {
        scaleShowGridLines: false,
        pointDot: false,
        datasetStroke: false,
        scaleFontSize: 8,
        scaleSteps: 10,
        bezierCurve: false,
    };

    // format the labels with the local date
    var labelFormat = function(value) {
        self.labels.push(dateFilter(value, 'M/d h:mma'));
    };

    // push the data
    var setData = function(value) {
        self.series.push(value.name);
        self.chartdata.push(value.data);
    };

    // Get the image json from pram
    ModHandlers.statistics.get({}, function(data) {
        self.data = data;
        // format the labels
        angular.forEach(data.labels, function(label) {
            labelFormat(label);
        });
        // set the data
        angular.forEach(data.series, function(data) {
            setData(data);
        });
    }, function(error) {
        Utils.apiError(error.status);
    });

}]);

