// errorHandler is the controller for the error page
angular.module('prim').controller('errorHandler', ["$scope", "Utils", function($scope, Utils) {

    // using controllerAs
    var self = this;

    // get the stored error code 
    self.errorcode = Utils.getError();

    if (angular.isUndefined(self.errorcode) || angular.equals(self.errorcode, "")) {
        self.errorcode = 404;
    }

    if (angular.equals(self.errorcode, -1)) {
        self.errorcode = 502;
    }

    switch (self.errorcode) {
        case 401 || 403:
            self.errormessage = "You are unauthorized to view this page";
            $scope.page.setTitle("Unauthorized");
            break;
        case 404:
            self.errormessage = "Couldn't find whatever you were looking for";
            $scope.page.setTitle("Not Found");
            break;
        case 502:
            self.errormessage = "Oh no the API server is probably down";
            $scope.page.setTitle("API Down");
            break;
        default:
            self.errormessage = "Looks like something went wrong";
            $scope.page.setTitle("Error");
            break;
    }

}]);

// DirectoryCtrl is the controller for the directory page
angular.module('prim').controller('DirectoryCtrl', ["$scope", "$routeParams", "$location", "data", "hotkeys", "Handlers", "AuthUtils", function($scope, $routeParams, $location, data, hotkeys, Handlers, AuthUtils) {

    // using controllerAs
    var self = this;

    // current page for pagination
    self.currentPage = $routeParams.page || 1;
    // watcher for pagination navigation
    $scope.$watch(function() {
        return self.currentPage;
    }, function(value, old) {
        if (!angular.equals(value, old)) {
            $location.path('/directory/' + value);
        }
    });

    // compare last active date
    self.getLastActive = AuthUtils.getLastActive;

    if (angular.isDefined(data)) {
        self.data = data.directory.items;
        // Pagination items from json
        self.pagination = {
            totalItems: data.directory.total,
            currentPage: data.directory.current_page,
            numPages: data.directory.pages,
            itemsPerPage: data.directory.per_page,
            maxSize: 3
        };
    }

    // set default column and order for table sorting
    self.sort = {
        column: 'last_post',
        desc: true
    };

    // reverse the sorting or change the column
    self.changeSorting = function(column) {
        var sort = self.sort;
        if (angular.equals(sort.column, column)) {
            sort.desc = !sort.desc;
        } else {
            sort.column = column;
            sort.desc = false;
        }
    };

    // async directory search
    self.searchThreads = function() {
        if (self.searchterm) {
            return Handlers.threadsearch.get({
                search: self.searchterm
            }).$promise.then(function(data) {
                self.data = data.threadsearch;
                return self.data;
            });
        }
    };

    hotkeys.bindTo($scope)
        .add({
            combo: 'shift+left',
            description: 'Previous Page',
            callback: function() {
                if (self.pagination.currentPage > 1) {
                    var page = self.pagination.currentPage - 1;
                    $location.path('/directory/' + page);
                }
            }
        })
        .add({
            combo: 'shift+right',
            description: 'Next Page',
            callback: function() {
                if (self.pagination.currentPage < self.pagination.numPages) {
                    var page = self.pagination.currentPage + 1;
                    $location.path('/directory/' + page);
                }
            }
        });

}]);

// provides functions for setting the users auth session
angular.module('prim').factory('AuthSession', ["$route", "toaster", "user_messages", "AuthStorage", "AuthState", "BoardData", "Handlers", "Utils", "UserHandlers", function($route, toaster, user_messages, AuthStorage, AuthState, BoardData, Handlers, Utils, UserHandlers) {

    var AuthSession = {
        // promise to the whoami handler
        queryWhoAmI: function() {
            return Handlers.whoami.get();
        },
        // handles the creation of key and cache store and also queries whoami handler for fresh info
        setAuthState: function() {
            // get the cached user data for smooth loading
            var cas = AuthStorage.getUserCache();
            // get the cached ib data
            var cib = BoardData.getIbData();

            // set our state to the cached version, or default if it isnt there
            if (cas && cib) {
                AuthState.set(cas.id, cas.name, cas.isAuthenticated, cas.avatar, cas.lastactive);
                BoardData.set(cib.group);
            } else {
                AuthState.default();
                BoardData.default();
            }

            // query whoami for user data
            AuthSession.queryWhoAmI().$promise.then(function(data) {
                var ss = data.user;

                // set auth state from whois information if authenticated
                if (ss.authenticated) {
                    AuthState.set(ss.id, ss.name, true, Utils.getAvatar(ss.id), new Date(ss.last_active));
                    // cache user data
                    AuthStorage.saveUserCache();

                    // set ib data from whois
                    BoardData.set(ss.group);
                    // cache ib data
                    BoardData.saveIbData();

                    return;
                }
            }, function() {
                // purge session if theres an error
                AuthStorage.destroySession();
                return;
            });
        },
        // log out the user
        logOut: function() {
            UserHandlers.logout.save();
            AuthStorage.destroySession();
            $route.reload();
            toaster.pop('success', user_messages.loggedOut);
        }
    };

    return AuthSession;

}]);

